package GUIBasic13;import javax.swing.*;import java.awt.*;/*** * Application of getContentPane() */public class FrameBackground {    public static void main(String[] args) {        JFrame jFrame = new JFrame();        jFrame.setTitle("JFrame tittle goes here");        jFrame.setSize(420, 420);        jFrame.setResizable(false);        jFrame.setLocationRelativeTo(null); /*Position center*/        jFrame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        jFrame.setVisible(true);        ImageIcon imageIcon = new ImageIcon("./Images/yousuf_logo.png");        jFrame.setIconImage(imageIcon.getImage());        jFrame.getContentPane().setBackground(new Color(100,100,100));    }}