package GUIBasic13;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;/** * JCheckBox: A GUI component that can be selected or deselected */public class MyCheckBox extends JFrame implements ActionListener {    JButton jButton;    JCheckBox jCheckBox;    MyCheckBox(){        jButton = new JButton();        jButton.setText("SUBMIT");        jButton.setOpaque(true);        jButton.setFont(new Font("Consolas", Font.BOLD, 20));        jButton.setBackground(Color.WHITE);        jButton.setForeground(Color.BLACK);        jButton.setFocusable(false);        jButton.addActionListener(this);        this.add(jButton);        jCheckBox = new JCheckBox();        jCheckBox.setText("I'm not a robot");        jCheckBox.setFocusable(false);        jCheckBox.setFont(new Font("Consolas", Font.PLAIN, 20));        jCheckBox.setOpaque(true);        this.add(jCheckBox);        this.setTitle("Check box example");        this.setSize(420, 420);        this.setLocation(720, 100);        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        this.setLayout(new FlowLayout());        this.getContentPane().setBackground(Color.DARK_GRAY);        this.setVisible(true);    }    @Override    public void actionPerformed(ActionEvent e) {        if(e.getSource() == jButton){            JOptionPane.showMessageDialog(null                    ,jCheckBox.isSelected()?"Yes, I'm not a robot.":"No, I'm a robot",                    "Message",                    JOptionPane.PLAIN_MESSAGE);        }    }    public static void main(String[] args) {        new MyCheckBox();    }}