package GUIBasic13;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.io.File;/**** * JFileChooser: A GUI mechanism that lets a user choose a file */class FileChooserDemo extends JFrame implements ActionListener {    JButton jButton;    FileChooserDemo(){        jButton = new JButton("Select File");        jButton.setFont(new Font("MV BOli", Font.BOLD, 15));        jButton.setBackground(Color.WHITE);        jButton.setForeground(Color.BLACK);        jButton.setFocusable(false);        jButton.addActionListener(this);        this.add(jButton);        this.setTitle("My file chooser demo");        this.setSize(420, 420);        this.setLocation(720, 100);        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        this.setLayout(new FlowLayout());        this.getContentPane().setBackground(Color.DARK_GRAY);        this.setVisible(true);    }    @Override    public void actionPerformed(ActionEvent e) {        if(e.getSource() == jButton){            JFileChooser jFileChooser = new JFileChooser();            int response = jFileChooser.showOpenDialog(this); /*Select file to open on own window*/            if(response == JFileChooser.APPROVE_OPTION){                File filePath = new File(jFileChooser.getSelectedFile().getAbsolutePath());                System.out.println(filePath);            }        }    }}public class MyFileChooser {    public static void main(String[] args) {        new FileChooserDemo();    }}