package GUIBasic13;import javax.swing.*;import java.awt.*;/*** * FlowLayout: Places components in a row, sized at their preferred size. * If the horizontal space in the container is too small, * the FlowLayout class uses the next available row. */public class MyFlowLayout extends JFrame {    public static void main(String[] args) {                MyFlowLayout frame = new MyFlowLayout();        frame.setTitle("Flow Layout Example");        frame.setSize(400, 400);        frame.setLocation(720, 100);        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        frame.getContentPane().setBackground(Color.DARK_GRAY);        frame.setLayout(new FlowLayout(FlowLayout.TRAILING, 10, 10)); /*By default, center*/                JPanel jPanel = new JPanel(new FlowLayout(FlowLayout.CENTER, 10, 10));        jPanel.setPreferredSize(new Dimension(150, 200));        jPanel.setToolTipText("Decimal Numbers");        jPanel.setBackground(Color.LIGHT_GRAY);        jPanel.add(new JButton("0"));        jPanel.add(new JButton("1"));        jPanel.add(new JButton("2"));        jPanel.add(new JButton("3"));        jPanel.add(new JButton("4"));        jPanel.add(new JButton("5"));        jPanel.add(new JButton("6"));        jPanel.add(new JButton("7"));        jPanel.add(new JButton("8"));        jPanel.add(new JButton("9"));        frame.add(jPanel);        frame.setVisible(true);    }}