package GUIBasic13;import javax.swing.*;public class MyFrameWithComponents {    public static void main(String[] args) {        JFrame frame = new JFrame("My frame with a components");        JButton okButton = new JButton("OK");        frame.setSize(400,300);        frame.setLocationRelativeTo(null); /*Center*/        frame.add(okButton);        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        frame.setVisible(true);    }}