package GUIBasic13;import javax.swing.*;import javax.swing.border.Border;import java.awt.*;/** * Image label, BorderFactory.createLineBorder(), Color */public class MyLabel extends JFrame {    public MyLabel(){        ImageIcon imageIcon = new ImageIcon("/SoftwaresCSE/JavaPrograms/GUIBasic13/img/yousuf_logo.png");        Border border = BorderFactory.createLineBorder(new Color(10, 150, 10), 10, true);        JLabel jLabel = new JLabel(); /*Create a label*/        jLabel.setText("Bro, do you even code?"); /*Set text of label*/        jLabel.setIcon(imageIcon);        jLabel.setHorizontalTextPosition(JLabel.CENTER); /*LEFT, RIGHT, CENTER of imageIcon*/        jLabel.setVerticalTextPosition(JLabel.TOP); /*TOP, CENTER, BOTTOM of imageIcon*/        jLabel.setForeground(new Color(10, 150, 10)); /*Set color of text*/        jLabel.setFont(new Font("MV Boli", Font.PLAIN, 20));  /*Set new font of text*/        jLabel.setIconTextGap(0); /*Set horizontal gap between img & text*/        jLabel.setBackground(new Color(10,10,10)); /*Set background color*/        jLabel.setOpaque(true); /*Display the background color*/        jLabel.setBorder(border);        jLabel.setVerticalAlignment(JLabel.CENTER); /*set vertical position of icon + text withing label*/        jLabel.setHorizontalAlignment(JLabel.CENTER); /*set horizontal position of icon + text withing label*/        this.add(jLabel);    }    public static void main(String[] args) {        MyLabel frame = new MyLabel();        frame.setSize(500, 500);        frame.setTitle("Label: Text, Image & Both");        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        frame.setLocationRelativeTo(null);        frame.setVisible(true);    }}