package GUIBasic13;import javax.swing.*;import java.awt.*;/*** * Progressbar: Visual aid to let the user know an operation is processed */class ProgressBarDemo extends JFrame {    JProgressBar jProgressBar;    ProgressBarDemo(){        jProgressBar = new JProgressBar();        jProgressBar.setValue(0);        jProgressBar.setBounds(0,150,420,50);        jProgressBar.setStringPainted(true);        jProgressBar.setFont(new Font("MV Boli", Font.BOLD, 25));        jProgressBar.setForeground(Color.RED);        jProgressBar.setBackground(Color.BLACK);        this.add(jProgressBar);        this.setTitle("Progressbar Demo");        this.setSize(420, 420);        this.setLocation(720, 100);        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        this.getContentPane().setBackground(Color.DARK_GRAY);        this.setLayout(null);        this.setVisible(true);        fillProgressBar();    }    public void fillProgressBar(){        int counter = 0;        while(counter <= 100){            jProgressBar.setValue(counter);            try{                Thread.sleep(100);            }            catch (InterruptedException interruptedException){                interruptedException.printStackTrace();  /*pinpoint the exact location of the exception*/            }            counter += 5;        }        jProgressBar.setString("Done!!!");    }}public class MyProgressBar {    public static void main(String[] args) {        new ProgressBarDemo();    }}