package GUIBasic13;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;/*** * JRadioButton: One or more buttons in a grouping in which only one 1 may be selected per group */public class MyRadioButton extends JFrame implements ActionListener {    JRadioButton pizzaButton;    JRadioButton hamburgerButton;    JRadioButton hotDogButton;    MyRadioButton(){        Font radioButtonFont = new Font("Consolas", Font.PLAIN, 20);        pizzaButton = new JRadioButton("Pizza");        pizzaButton.setFont(radioButtonFont);        pizzaButton.setFocusable(false);        pizzaButton.addActionListener(this);        hamburgerButton = new JRadioButton("Hamburger");        hamburgerButton.setFont(radioButtonFont);        hamburgerButton.setFocusable(false);        hamburgerButton.addActionListener(this);        hotDogButton = new JRadioButton("Hot Dog");        hotDogButton.setFont(radioButtonFont);        hotDogButton.setFocusable(false);        hotDogButton.addActionListener(this);        ButtonGroup  buttonsGroup = new ButtonGroup(); /*Its has no J prefix but this is swing component*/        buttonsGroup.add(pizzaButton);        buttonsGroup.add(hamburgerButton);        buttonsGroup.add(hotDogButton);        this.add(pizzaButton);        this.add(hamburgerButton);        this.add(hotDogButton);        this.setTitle("Radio Button Example");        this.setSize(420, 420);        this.setLocation(720, 100);        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        this.setLayout(new FlowLayout(FlowLayout.CENTER, 20,50));        this.getContentPane().setBackground(Color.DARK_GRAY);        this.setVisible(true);    }    @Override    public void actionPerformed(ActionEvent e) {        if(e.getSource() == pizzaButton){            showMessage("You ordered a pizza.");        }        else if(e.getSource() == hamburgerButton){            showMessage("You ordered a hamburger.");        }        else if(e.getSource() == hotDogButton){            showMessage("You ordered a hot dog.");        }    }    /*I have to show position of center.*/    public static void showMessage(String message){        JOptionPane.showMessageDialog(null,                message,                "Simple Message",                JOptionPane.INFORMATION_MESSAGE);    }    public static void main(String[] args) {        new MyRadioButton();    }}