package GUIBasic13;import javax.swing.*;import javax.swing.event.ChangeEvent;import javax.swing.event.ChangeListener;import java.awt.*;/*** * JSlider: GUI components that lets user enter a value * by using an adjustable sliding knob on a track */class SliderDemo extends JFrame implements ChangeListener {    int temperature;    JPanel jPanel;    JLabel jLabel;    JSlider jSlider;    SliderDemo(){        jPanel = new JPanel();        jLabel = new JLabel();        jSlider = new JSlider(0, 100, 50);        jSlider.setPreferredSize(new Dimension(400, 100));        jSlider.setPaintTicks(true);        jSlider.setMinorTickSpacing(1);        jSlider.setPaintTrack(true);        jSlider.setMajorTickSpacing(10);        jSlider.setPaintLabels(true);        jSlider.setFont(new Font("MV Boli", Font.PLAIN, 15));        jSlider.setOrientation(SwingConstants.HORIZONTAL); /*By default*/        jSlider.addChangeListener(this);        jLabel.setText("^C "+jSlider.getValue());        jLabel.setOpaque(true);        jLabel.setFont(new Font("MV Boli", Font.BOLD, 20));        jPanel.add(jSlider);        this.add(jLabel);        this.add(jPanel);        this.setTitle("Slider Demo");        this.setSize(420, 420);        this.setLocation(720, 100);        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        this.getContentPane().setBackground(Color.DARK_GRAY);        this.setLayout(new FlowLayout(FlowLayout.CENTER, 20, 50));        this.setVisible(true);    }    @Override    public void stateChanged(ChangeEvent e) {        if (e.getSource() == jSlider){            temperature = jSlider.getValue();            if(temperature >= 60 && temperature <= 70){                jLabel.setForeground(Color.YELLOW);                jLabel.setBackground(Color.LIGHT_GRAY);                jLabel.setText("^C "+temperature);            }            else if(temperature >= 71 && temperature <= 100){                jLabel.setForeground(Color.RED);                jLabel.setBackground(Color.LIGHT_GRAY);                jLabel.setText("^C "+temperature);            }            else if(temperature >= 0 && temperature <= 10){                jLabel.setForeground(Color.RED);                jLabel.setBackground(Color.LIGHT_GRAY);                jLabel.setText("^C "+temperature);            }            else if(temperature >= 11 && temperature <= 20){                jLabel.setForeground(Color.YELLOW);                jLabel.setBackground(Color.LIGHT_GRAY);                jLabel.setText("^C "+temperature);            }            else if(temperature >= 21 && temperature <= 30){                jLabel.setForeground(Color.GREEN);                jLabel.setBackground(Color.LIGHT_GRAY);                jLabel.setText("^C "+temperature);            }            else{                jLabel.setForeground(Color.WHITE);                jLabel.setBackground(Color.BLACK);                jLabel.setText("^C "+temperature);            }        }    }}public class MySliderDemo {    public static void main(String[] args) {        new SliderDemo();    }}