package GUIBasic13;import javax.swing.*;import java.awt.*;public class ShowBorderLayout extends JFrame {    public ShowBorderLayout(){        this.setLayout(new BorderLayout(5, 10));        this.add(new JButton("East"), BorderLayout.EAST);        this.add(new JButton("West"), BorderLayout.WEST);        this.add(new JButton("North"), BorderLayout.NORTH);        this.add(new JButton("South"), BorderLayout.SOUTH);        this.add(new JButton("Center"), BorderLayout.CENTER);    }    public static void main(String[] args) {        ShowBorderLayout showBorderLayout = new ShowBorderLayout();        showBorderLayout.setTitle("Show Border Layout");        showBorderLayout.setSize(100, 200);        showBorderLayout.pack();        showBorderLayout.setLocationRelativeTo(null);        showBorderLayout.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        showBorderLayout.setVisible(true);    }}