package GUIBasic13;import javax.swing.*;import java.awt.*;/** * GridLayout with JLabel */public class TestImageIcon extends JFrame {    public TestImageIcon(){        setLayout(new GridLayout(1,4, 5, 5));        ImageIcon afganFlagImageIcon = new ImageIcon("/SoftwaresCSE/JavaPrograms/GUIBasic13/Images/Afghan.jpeg");        add(new JLabel(afganFlagImageIcon));        ImageIcon pakFlagImageIcon = new ImageIcon("/SoftwaresCSE/JavaPrograms/GUIBasic13/Images/Pak.jpeg");        add(new JLabel(pakFlagImageIcon));        ImageIcon bdFlagImageIcon = new ImageIcon("/SoftwaresCSE/JavaPrograms/GUIBasic13/Images/BD.png");        add(new JLabel(bdFlagImageIcon));        ImageIcon malaysiaFlagImageIcon = new ImageIcon("/SoftwaresCSE/JavaPrograms/GUIBasic13/Images/Malaysia.png");        add(new JLabel(malaysiaFlagImageIcon));    }    public static void main(String[] args) {        JFrame frame = new TestImageIcon();        frame.setSize(500, 200);        frame.setTitle("Test Image Icon");        frame.setLocationRelativeTo(null);        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        frame.setVisible(true);    }}